import React from 'react';
import './General.css';
import { useForm } from "react-hook-form";
const General = () => {
    const { register, handleSubmit } = useForm({

    });
    const adminToken='eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJpc3MiOiJodHRwczpcL1wvc2Uuc2Vsb3BpYW4udXNcL2FwaVwvYWRtaW5cL2xvZ2luIiwiaWF0IjoxNjc1MTYxNTgwLCJleHAiOjE2NzUxNjUxODAsIm5iZiI6MTY3NTE2MTU4MCwianRpIjoiOXlsemZVb2tlYVJhcXpTcCIsInN1YiI6MSwicHJ2IjoiZGY4ODNkYjk3YmQwNWVmOGZmODUwODJkNjg2YzQ1ZTgzMmU1OTNhOSJ9.xPYKbVWUBbq9G07FjGiBJsy6jC5QCLb7LUdjHwx9s18';
    const handleUpload = (event) => {
        event.preventDefault();
        const site_name = event.target.site_name.value;
        const site_email = event.target.site_email.value;
        const site_phone = event.target.site_phone.value;
        const site_address=event.target.site_address.value;
        const site_facebook = event.target.site_facebook.value;
        const site_twitter = event.target.site_twitter.value;
        const site_linkedin= event.target.site_linkedin.value;
        const site_instagram=event.target.site_instagram.value;

        const site_youtube = event.target.site_youtube.value;
        const site_pinterest = event.target.site_pinterest.value;
        const site_about= event.target.site_about.value;
        const logo=event.target.logo.files[0];
        const favicon=event.target.favicon.files[0];
        console.log(logo,favicon);
        const url = 'https://se.selopian.us/api/admin/general-setting-update';

        //fetch data
        const postData = {
            site_name: site_name,
            site_email:site_email,
            site_phone:site_phone,
            site_address:site_address,
            site_facebook:site_facebook,
            site_twitter:site_twitter,
            site_linkedin:site_linkedin,
            site_youtube:site_youtube,
            site_pinterest:site_pinterest,

        }
        const formData = new FormData();
        formData.append("data", postData);
        formData.append("logo", logo);
        formData.append("favicon", favicon);

        fetch(url, {
            method: 'POST',
            body:formData,
            headers: {
                headers:{
                    'Authorization':`bearer ${adminToken}`},
            },
        })
            .then((response) => response.json())
            .then((data) => {
                console.log(data)
                event.target.reset();
            });
    }
    return (


     <div>
         <form >
             <input
                 ref={register}
                 placeholder="First Name"
                 type="text"
                 name="firstname"
             />
             <input
                 ref={register}
                 placeholder="Last Name"
                 type="text"
                 name="lastname"
             />
             <button>Submit</button>
         </form>
     </div>


    );
};

export default General;
