                'Authorization': `bearer ${adminToken}`
            }
        })
        const data = await res.json()
        if (res.status === 200) {
            console.log(data.data);
            setDetails(data.data)
            console.log(details)
        }
    }
    useEffect(() => {
        getDetails();
    }, [adminToken]);
